mapper.sql:
SELECT category_id categoryId,single_disease_name disease,sum(need_report_count) needReportCount, sum(complete_report_count) completeReportCount,sum(not_report_count) notReportCount, CONCAT(IFNULL(CAST(AVG(complete_report_country_rate) AS DECIMAL(18,2)),0),'%') completeReportCountryRate, CAST(sum(average_in_hospital_day) AS DECIMAL(18,2)) averageInHospitalDay, CAST(sum(average_in_hospital_fee) AS DECIMAL(18,2)) averageInHospitalFee, CAST(sum(average_drug_fee) AS DECIMAL(18,2)) averageDrugFee, CAST(sum(average_operation_treatment_fee) AS DECIMAL(18,2)) averageOperationTreatmentFee, CAST(sum(average_disposable_consumable) AS DECIMAL(18,2)) averageDisposableConsumable, CONCAT(CAST(sum(mortality) AS DECIMAL(18,2)),'%') mortality, CONCAT(CAST(sum(operation_complication_rate) AS DECIMAL(18,2)),'%') operationComplicationRate FROM `q_single_disease_statistic_hospital`
year_month_remark >= #{dateStart} and year_month_remark <= #{dateEnd}
and category_id in
#{listItem}
group by categoryId

mapper.select:
SELECT	single_disease_name disease,sum(complete_report_count) number FROM `q_single_disease_statistic_hospital`


        year_month_remark >= #{qSingleDiseaseTakeReportQuantityRankingParam.dateStart} and year_month_remark <= #{qSingleDiseaseTakeReportQuantityRankingParam.dateEnd}


group by category_id order by number desc

SELECT	single_disease_name disease,CAST(avg(complete_report_country_rate) AS DECIMAL(18,2)) number FROM `q_single_disease_statistic_hospital`


        year_month_remark >= #{qSingleDiseaseTakeReportQuantityRankingParam.dateStart} and year_month_remark <= #{qSingleDiseaseTakeReportQuantityRankingParam.dateEnd}


group by category_id order by number desc
